  <View style={styles.container}>
    <ImageBackground source={bg} resizeMode="cover" style={styles.image}></ImageBackground>
  
    {/* //view to hold the text fields and button */}
  <View style={styles.form}> 
    <TextInput 
        multiline
        name = "uname"
        value = {uname}
        style={styles.input}
        placeholder="username"
        onChangeText={setUname}
       />
    <TextInput 
        name = "password"
        value ={password}
        multiline
        style={styles.input}
        caretHidden
        placeholder="password"
        onChangeText={setPassword}


        onChangeText = {getPassword}/>
      <TouchableOpacity
        onPress={() => {
          alert("Logged In as " + uname); // button event handles to the SignUp screen
          navigation.navigate("Home");
        }}
        style={{
          padding: 15,
          paddingHorizontal: 80,
          marginTop: 10,
          alignItems: "center",
          borderRadius: 25,
          flexDirection: "row",
          backgroundColor: "black",
        }}
      >
      
        <Text style={{ paddingLeft: 10, color: "white" }}>Log In </Text>
      </TouchableOpacity>
        </View>
    </View>
  
);
    };
const styles = StyleSheet.create({
  container: {
